import matplotlib.pyplot as pltimport osdef plot_token_distribution(token_df, output_dir):    """    Plots and saves the token distributions for each persona as bar charts using a common token grid.        Args:    - token_df: DataFrame containing token distributions with Persona, Token, Count columns.    - output_dir: Directory to save the plots.    """    # Create the common token grid    persona_token_matrix = token_df.pivot_table(index='Persona', columns='Token', values='Count', fill_value=0)        if not os.path.exists(output_dir):        os.makedirs(output_dir)        # Iterate through each persona and create a bar plot    for persona in persona_token_matrix.index:        persona_data = persona_token_matrix.loc[persona]                plt.figure(figsize=(10, 6))        plt.bar(persona_data.index, persona_data.values)        plt.title(f"Token Distribution for {persona}")        plt.xlabel('Token')        plt.ylabel('Count')        plt.xticks(rotation=90)                plot_file = os.path.join(output_dir, f'{persona}_token_distribution.png')        plt.tight_layout()        plt.savefig(plot_file)        plt.close()                print(f"Token distribution plot saved to {plot_file}")