# token_analysis/token_visualization.pyimport matplotlib.pyplot as pltdef plot_token_distribution(token_counts, persona_name):    tokens, counts = zip(*token_counts.items())    plt.bar(tokens, counts)    plt.title(f"Token Distribution for {persona_name}")    plt.xlabel("Tokens")    plt.ylabel("Frequency")    plt.xticks(rotation=90)    plt.tight_layout()    plt.show()def plot_persona_comparison(common_tokens, unique_to_p1, unique_to_p2, persona1, persona2):    labels = ['Common', f'Unique to {persona1}', f'Unique to {persona2}']    sizes = [len(common_tokens), len(unique_to_p1), len(unique_to_p2)]    plt.pie(sizes, labels=labels, autopct='%1.1f%%', startangle=140)    plt.axis('equal')    plt.show()
